var data = msg.payload[0];
var basedatos = 'wisensor_aquachile';
var codigo_empresa = data.empresa;
var codigo_centro = data.codigo_centro;

var query = "SELECT  a.id as alerta_id, e.mongodb, e.codigo as codigo_empresa, cc.codigo_interno, a.created_at as fecha,  cc.latitud, cc.longitud, cc.heading_inicial," +
"sv.nombre_real, a.tipo_alerta, cc.ip_port, a.tipo_dato, a.valor1, a.valor2, a.valor3, a.valor4, a.checkemail, a.checkbuzzer, a.centros_id, sv.nombre_visible, sv.simbolo, c.nombre" +
" FROM clima_alertas AS a" +
" INNER JOIN centros AS c ON a.centros_id = c.id" +
" INNER JOIN empresas AS e ON c.empresas_id = e.id" +
" INNER JOIN sistema_variables AS sv ON a.sistema_variables_id = sv.id" +
" INNER JOIN centros_concesiones AS cc ON a.centros_id = cc.centros_id" +
" WHERE a.activo = 1 " +
" AND c.activo = 1" +
" AND a.activo = 1" +
" AND cc.activo = 1" +
" AND e.mongodb = '"+basedatos +"' "+
" AND cc.codigo_interno = '" + codigo_centro + "' " +
" AND e.codigo = '" + codigo_empresa + "' " +
" AND a.deleted_at IS NULL" +
" AND c.deleted_at IS NULL"+
" AND e.deleted_at IS NULL";
msg.topic = query;
msg.data = data;
return msg;

