var newMsg = [];
var alertas = msg.alertas;
var arr = msg.data;
var notificacion = [];

for (let index = 0; index < alertas.length; index++) {
    var alerta_id = alertas[index].alerta_id;
    var variable = alertas[index].variable;
    var variable_visible = alertas[index].nombre_visible;
    var simbolo = alertas[index].simbolo;
    var database = alertas[index].mongodb;
    var tipo_dato = alertas[index].tipo_dato;
    var valor1 = alertas[index].valor1;
    var valor2 = alertas[index].valor2;
    var valor3 = alertas[index].valor3;
    var valor4 = alertas[index].valor4;
    var tipo_alerta = alertas[index].tipo_alerta;
    var ip_port = alertas[index].ip_port;
    var centro = alertas[index].centro;

    var checkemail = alertas[index].checkemail;
    var checkbuzzer = alertas[index].checkbuzzer; 
    var centros_id = alertas[index].centros_id; 
    var checksms = 1;

    var latitud = alertas[index].latitud;
    var longitud = alertas[index].longitud;

    switch (tipo_dato) {
        case 'Integer':
            valor1 = parseInt(valor1);
            valor2 = parseInt(valor2);
            valor3 = valor3 != null ? parseInt(valor3) : null;
            valor4 = valor4 != null ? parseInt(valor4) : null;
            break;
        case 'Float':
            valor1 = parseFloat(valor1);
            valor2 = parseFloat(valor2);
            valor3 = valor3 != null ? parseFloat(valor3) : null;
            valor4 = valor4 != null ? parseFloat(valor4) : null;
            break;
    }

    var severidad = '';
    if (variable == 'desviacionGPS') {
        if (tipo_alerta == 'Simple'){
            var long = arr.datos[variable].coordinates[0];
            var latit = arr.datos[variable].coordinates[1];
            var distancia = geolib.getPreciseDistance(
                { latitude: latitud, longitude: longitud },
                { latitude: latit, longitude: long },
            );
            if (valor1 > 0) {
                //positivo
                if (distancia >= valor1) {
                    severidad = 'Rojo';
                } else {
                    if (distancia >= valor2 && distancia < valor1) {
                        severidad = 'Amarillo';
                    } else {
                        severidad = 'Verde';
                    }
                }
            }

            if (severidad != 'Verde' && severidad != '') {
                var unid = '';
                var dist = 0;
                if(distancia < 1000){
                    unid = 'm';
                    dist = distancia;
                } else {
                    unid = 'km';
                    dist = geolib.convertDistance(distancia, 'km');
                }
                notificacion = {
                    centro: centro,
                    variable: variable_visible,
                    valor: dist,
                    unidad_medida: unid,
                    fecha: new moment(arr.fecha).format("DD/MM/YYYY, h:mm:ss a"),
                    severidad: severidad
                };

                qry_insert = {
                    clima_alertas_id: alerta_id,
                    mongodb_id: String(arr._id),
                    severidad: severidad,
                    fecha: arr.fecha,
                    visto: false,
                    users_id: null,
                    respuestas_alertas_id: null,
                    comentario: ''
                };

                newMsg.push({
                    insert_data: qry_insert,
                    checkemail: checkemail,
                    checkbuzzer: checkbuzzer,
                    checksms: checksms,  
                    centros_id: centros_id,
                    ip_port: ip_port,
                    notificacion: notificacion
                });

            }
        }
    } else if (variable == 'heading'){
        if (tipo_alerta == 'Combinada') {
            var heading_error = 0;
            var setpoint = parseFloat(alertas[index].heading_inicial);
            var heading_actual = parseFloat(arr.datos[variable]);
            heading_error = setpoint - heading_actual;

            if(heading_error <= valor1 || heading_error >= valor4){
                severidad = 'Rojo';
            } else {
                if ((heading_error > valor1 && heading_error <= valor2) || (heading_error >= valor3 && heading_error < valor4)) {
                    severidad = 'Amarillo';
                } else {
                    severidad = 'Verde';
                }
            }

            if (severidad != 'Verde' && severidad != '') {

