                });

            }
        }

    } else {
        switch (tipo_alerta) {
            case 'Simple':
                if (valor1 > 0) {
                    //positivo
                    if (arr.datos[variable] >= valor1) {
                        severidad = 'Rojo';
                    } else {
                        if (arr.datos[variable] >= valor2 && arr.datos[variable] < valor1) {
                            severidad = 'Amarillo';
                        } else {
                            severidad = 'Verde';
                        }
                    }
                } else {
