                } else {
                    if ((arr.datos[variable] >= valor1 && arr.datos[variable] < valor2) || (arr.datos[variable] > valor3 && arr.datos[variable] < valor4)) {
                        severidad = 'Amarillo';
                    } else {
                        severidad = 'Verde';
                    }
                }
                break;
        }
        if (severidad != 'Verde' && severidad != '') {
            notificacion = {
